mapper.insert:
insert into post (

    title, 
    content, 
    author_id, 

) values (

    #{title}, 
    #{content}, 
    #{author.userId}, 

);

mapper.select:
select p.post_id as post_id, p.title as title, p.content as content from post as p
and p.author_id = #{userId}
limit #{offset}, #{limit}

mapper.insert:
insert into post_tag (

    tag_id, 
    post_id, 

) values (

    #{tagId}, 
    #{postId}, 

);
