mapper.insert:
insert into user (
username, email, password, role_id,
) values (
#{username}, #{email}, #{password}, #{role.roleId},
);

mapper.select:
select user_id, username, email from user as u
and user_id = #{id}
limit 1

select user_id, username, email, p.post_id as p_post_id, p.title as p_title from user as u left join post as p on p.author_id = u.user_id
and u.user_id = #{id}
order by p.post_id desc
